// Pegar o item do merge
const item = $input.first().json;

// Determinar a source correta dos dados
let contactId, companyId, whatsappId, contactName, messageContent, messageType;
let phoneNumber, evolutionApiUrl, evolutionInstance, evolutionApiKey, originSource, optOut, messageId, pushName;

// Se veio do Execute: Create Contact (tem success: true e contact_id)
if (item.success && item.contact_id) {
  const context = $('Enrich: Message Context').first().json;

  contactId = item.contact_id;
  companyId = context.company_id;
  whatsappId = context.whatsapp_id;
  contactName = context.contact_name;
  messageContent = context.message_content;
  messageType = context.message_type;
  phoneNumber = context.phone_number;
  evolutionApiUrl = context.evolution_api_url;
  evolutionInstance = context.evolution_instance;
  evolutionApiKey = context.evolution_api_key;
  originSource = context.origin_source;
  optOut = false;
  messageId = context.messageId || context.message_id;  // âœ… CORRIGIDO
  pushName = context.pushName || context.push_name;      // âœ… CORRIGIDO
}
// Se veio do Execute: Reactivate
else if (item.contactId && item.message) {
  const context = $('Enrich: Message Context').first().json;

  contactId = item.contactId;
  companyId = context.company_id;
  whatsappId = context.whatsapp_id;
  contactName = context.contactName || context.contact_name;
  messageContent = context.message_content;
  messageType = context.message_type;
  phoneNumber = context.phone_number;
  evolutionApiUrl = context.evolution_api_url;
  evolutionInstance = context.evolution_instance;
  evolutionApiKey = context.evolution_api_key;
  originSource = context.origin_source;
  optOut = false;
  messageId = context.messageId || context.message_id;  // âœ… CORRIGIDO
  pushName = context.pushName || context.push_name;      // âœ… CORRIGIDO
}
// Se veio do Execute: Process Commands
else if (item.command_executed) {
  const context = $('Enrich: Message Context').first().json;

  contactId = item.contact_id;
  companyId = context.company_id;
  whatsappId = context.whatsapp_id;
  contactName = context.contact_name;
  messageContent = context.message_content;
  messageType = context.message_type;
  phoneNumber = context.phone_number;
  evolutionApiUrl = context.evolution_api_url;
  evolutionInstance = context.evolution_instance;
  evolutionApiKey = context.evolution_api_key;
  originSource = context.origin_source;
  optOut = item.opt_out || false;
  messageId = context.messageId || context.message_id;  // âœ… CORRIGIDO
  pushName = context.pushName || context.push_name;      // âœ… CORRIGIDO
}
// Se veio direto (active_chat)
else {
  contactId = item.contact_id;
  companyId = item.company_id;
  whatsappId = item.whatsapp_id;
  contactName = item.contact_name;
  messageContent = item.message_content;
  messageType = item.message_type;
  phoneNumber = item.phone_number;
  evolutionApiUrl = item.evolution_api_url;
  evolutionInstance = item.evolution_instance;
  evolutionApiKey = item.evolution_api_key;
  originSource = item.origin_source;
  optOut = item.opt_out;
  messageId = item.messageId || item.message_id;        // âœ… CORRIGIDO
  pushName = item.pushName || item.push_name;            // âœ… CORRIGIDO
}

// Pegar TODOS os dados de Enrich: Message Context (incluindo base64, media, etc)
const enrichContext = $('Enrich: Message Context').first().json;
const normalizeData = $('Execute: Normalize Evolution Data').first().json;

// Pegar base64 corretamente
const base64Data = normalizeData?.body?.data?.message?.base64 || enrichContext?.body?.data?.message?.base64 || null;

return [{
  json: {
    contact_id: contactId,
    company_id: companyId,
    whatsapp_id: whatsappId,
    message_id: messageId,                              // âœ… Agora sempre terÃ¡ valor
    contact_name: contactName,
    message_content: messageContent,
    message_type: messageType,
    phone_number: phoneNumber,
    evolution_api_url: evolutionApiUrl,
    evolution_instance: evolutionInstance,
    evolution_api_key: evolutionApiKey,
    origin_source: originSource,
    opt_out: optOut,
    contact_exists: enrichContext.contact_exists,
    quoted_message: enrichContext.quoted_message,
    transcribed: enrichContext.transcribed,
    has_media: enrichContext.has_media,
    media_url: enrichContext.media_url,
    media_mime_type: enrichContext.media_mime_type,
    media_type: enrichContext.media_type,
    caption: enrichContext.caption,
    sender_type: normalizeData.sender_type,
    'body.data.message.base64': base64Data
  }
}];
